import React, { useContext } from 'react';
import { observer } from "mobx-react-lite";
import { Context } from "../index";
import { Card, Row } from "react-bootstrap";
import moduleStyle from "../modules/NationalityBar.module.css"

const NationalityBar = observer(() => {
    const { course } = useContext(Context)
    return (
        <Row className={moduleStyle.cardRow}>
            {course.language.map(nationality => <Card className={moduleStyle.card}
                style={{ cursor: "pointer" }}
                text={nationality.id === course.selectedNationality.id ? "danger" : "light"}
                onClick={() => course.setSelectedNationality(nationality)}
                key={nationality.id}>
                {nationality.name}
            </Card>)}
        </Row>
    );
});

export default NationalityBar;
