import { $authHost, $host } from "./index";
import jwt_decode from 'jwt-decode';


export const registration = async (email, password, nickname) => {
    const { data } = await $host.post('api/user/registration', { email, password, nickname })
    localStorage.setItem('token', data.token)
    return jwt_decode(data.token)
}

export const login = async (email, password) => {
    const { data } = await $host.post('api/user/login', { email, password })
    localStorage.setItem('token', data.token)
    const token = jwt_decode(data.token)
    localStorage.setItem('userId', token.id)
    console.log(localStorage.getItem('userId'))
    return token
}

export const fetchName = async (userId) => {
    if (userId != null) {
        const { data } = await $host.get('api/user/name/' + userId)
        return data
    } else {
        return null
    }
}

export const check = async () => {
    const { data } = await $authHost.get('api/user/auth')
    localStorage.setItem('token', data.token)
    return jwt_decode(data.token)
}
