import React, { useEffect, useLayoutEffect, useState } from 'react';
import { Col, Container, Image, Row, Card, Button, CardBody, CardTitle, CardText } from "react-bootstrap";
import { Link, useParams } from "react-router-dom";
import { fetchOneCourse, fetchOneCourseDetails } from "../http/courseAPI";
import WebSock from "../components/WebSock";

const CoursePage = () => {
    const [course, setCourse] = useState({});
    const [info, setInfo] = useState([]);
    const { id } = useParams();

    const descriptionStyle = {
        color: '#333',
        fontSize: '18px',
        fontFamily: 'Helvetica Neue", Helvetica, Arial, sans-serif',
        lineHeight: '1.6',
        backgroundColor: 'light teal', // light blue background
        padding: '20px',
        margin: '20px auto',
        maxWidth: '800px',
        borderRadius: '10px',
        boxShadow: '0 4px 12px rgba(0, 0, 0, 0.1)',
        textAlign: 'left', // left-align text
        // borderLeft: '8px solid #87cefa'

    }

    useLayoutEffect(() => {
        fetchOneCourse(id).then(data => setCourse(data[0]));
        fetchOneCourseDetails(id).then(data => setInfo(data));
    }, [id]);

    return (
        <Container>
            <Row className='mt-3'>
                <Col md={6} >
                    <p style={{ fontFamily: 'sans - serif', fontSize: '20px' }}>Course: {course.teacherName}</p>
                    <h1>{course.title}</h1>
                    <Image src={process.env.REACT_APP_API_URL + course.img}
                        style={{ backgroundSize: "cover", width: '400px', borderRadius: '15px' }} />
                </Col>
                <Col md={3} style={descriptionStyle}>
                    <h2 style={{ fontFamily: 'sans - serif' }}>Description</h2>
                    <p style={{ fontFamily: 'sans - serif' }}>{course.description}</p>
                </Col>
                <Row />
                <Card style={{ color: 'black', backgroundColor: 'teal' }}>
                    <CardBody>
                        <CardTitle>sing in to the cousre.</CardTitle>
                        <CardText>start a new journy learning a new language.</CardText>
                        <Button variant='primary'>оставить заивку</Button>
                    </CardBody>
                </Card>

            </Row>
            {/* <Row className="mt-5">
                <h2>Course Details</h2>
                {
                    info.map((i, index) =>
                        <Row key={index} style={{ backgroundColor: index % 2 === 0 ? '#808B96 ' : 'transparent', padding: 10 }} >
                            <Col>
                                {i.detail}
                            </Col>
                            <Col>{i.value}</Col>
                        </Row>
                    )
                }
            </Row> */}
        </Container >
    );
};

export default CoursePage;
