import React, { useEffect, useLayoutEffect, useState } from 'react';
import { Col, Container, Image, Row } from "react-bootstrap";
import { Link, useParams } from "react-router-dom";
import { fetchOneCourse, fetchOneCourseDetails } from "../http/courseAPI";
import WebSock from "../components/WebSock";

const CoursePage = () => {
    const [course, setCourse] = useState({});
    const [info, setInfo] = useState([]);
    const { id } = useParams();


    useLayoutEffect(() => {
        fetchOneCourse(id).then(data => setCourse(data[0]));
        fetchOneCourseDetails(id).then(data => setInfo(data));
    }, [id]);

    return (
        <Container>
            <Row className='mt-3'>
                <Col md={6}>
                    <p>Course: {course.teacherName}</p>
                    <h1>{course.title}</h1>
                    <Image src={process.env.REACT_APP_API_URL + course.img}
                        style={{ backgroundSize: "cover", width: '400px', borderRadius: '15px' }} />
                </Col>
                <Col md={3}>
                    <h2>Description</h2>
                    <p>{course.description}</p>
                </Col>
                <Row />

            </Row>
            {/* <Row className="mt-5">
                <h2>Course Details</h2>
                {
                    info.map((i, index) =>
                        <Row key={index} style={{ backgroundColor: index % 2 === 0 ? '#808B96 ' : 'transparent', padding: 10 }} >
                            <Col>
                                {i.detail}
                            </Col>
                            <Col>{i.value}</Col>
                        </Row>
                    )
                }
            </Row> */}
        </Container>
    );
};

export default CoursePage;
